    const title = section.title;
    const subtext = section.subtext;
    var classname = "Meoi-link";
    if(selected.key === section.key) {
        classname = "Meoi-link-selected";
    }
    return (
        <div><a
        className={classname}
        href="https://reactjs.org"
      >
        {title}
      </a></div>);
  }
export default LinkSection;
